// hooks and interceptors per client, for fast access.
type (
	hooks struct {
		APIKey, Channel, Job, Request, RequestExecution, User []ent.Hook
	}
	inters struct {
		APIKey, Channel, Job, Request, RequestExecution, User []ent.Interceptor
	}
)
