     1→package api
     2→
     3→import (
     4→	"github.com/gin-gonic/gin"
     5→
     6→	"github.com/looplj/axonhub/internal/log"
     7→	"github.com/looplj/axonhub/internal/pkg/httpclient"
     8→	"github.com/looplj/axonhub/internal/pkg/streams"
     9→	"github.com/looplj/axonhub/internal/server/chat"
    10→)
    11→
    12→type ChatCompletionErrorHandler interface {
    13→	// HandlerError handles error in non-stream response, should return the proper error format to client.
    14→	HandlerError(c *gin.Context, err error)
    15→}
    16→
    17→type ChatCompletionSSEHandlers struct {
    18→	ChatCompletionProcessor *chat.ChatCompletionProcessor
    19→	ErrorHandler            ChatCompletionErrorHandler
    20→}
    21→
    22→func NewChatCompletionHandlers(processor *chat.ChatCompletionProcessor) *ChatCompletionSSEHandlers {
    23→	return &ChatCompletionSSEHandlers{
    24→		ChatCompletionProcessor: processor,
    25→	}
    26→}
    27→
    28→func (handlers *ChatCompletionSSEHandlers) ChatCompletion(c *gin.Context) {
    29→	ctx := c.Request.Context()
    30→
    31→	// Use ReadHTTPRequest to parse the request
    32→	genericReq, err := httpclient.ReadHTTPRequest(c.Request)
    33→	if err != nil {
    34→		handlers.ErrorHandler.HandlerError(c, err)
    35→		return
    36→	}
    37→
    38→	result, err := handlers.ChatCompletionProcessor.Process(ctx, genericReq)
    39→	if err != nil {
    40→		handlers.ErrorHandler.HandlerError(c, err)
    41→		return
    42→	}
    43→
    44→	if result.ChatCompletion != nil {
    45→		resp := result.ChatCompletion
    46→
    47→		contentType := "application/json"
    48→		if ct := resp.Headers.Get("Content-Type"); ct != "" {
    49→			contentType = ct
    50→		}
    51→
    52→		c.Data(resp.StatusCode, contentType, resp.Body)
    53→
    54→		return
    55→	}
    56→
    57→	if result.ChatCompletionStream != nil {
    58→		defer func() {
    59→			log.Debug(ctx, "Close chat stream")
    60→
    61→			err := result.ChatCompletionStream.Close()
    62→			if err != nil {
    63→				logger.Error(ctx, "Error closing stream", log.Cause(err))
    64→			}
    65→		}()
    66→
    67→		// Set SSE headers
    68→		c.Header("Content-Type", "text/event-stream")
    69→		c.Header("Cache-Control", "no-cache")
    70→		c.Header("Connection", "keep-alive")
    71→		c.Header("Access-Control-Allow-Origin", "*")
    72→
    73→		writeSSEStream(c, result.ChatCompletionStream)
    74→	}
    75→}
    76→
    77→func writeSSEStream(c *gin.Context, stream streams.Stream[*httpclient.StreamEvent]) {
    78→	ctx := c.Request.Context()
    79→	clientDisconnected := false
    80→
    81→	defer func() {
    82→		if clientDisconnected {
    83→			log.Warn(ctx, "Client disconnected")
    84→		}
    85→	}()
    86→
    87→	clientGone := c.Writer.CloseNotify()
    88→
    89→	for {
    90→		select {
    91→		case <-clientGone:
    92→			clientDisconnected = true
    93→			log.Warn(ctx, "Client disconnected")
    94→			return
    95→		default:
    96→			if stream.Next() {
    97→				cur := stream.Current()
    98→				c.SSEvent(cur.Type, cur.Data)
    99→				log.Debug(ctx, "write stream event", log.Any("event", cur))
   100→				c.Writer.Flush()
   101→			} else {
   102→				if stream.Err() != nil {
   103→					log.Error(ctx, "Error in stream", log.Cause(stream.Err()))
   104→					c.SSEvent("error", stream.Err())
   105→				}
   106→
   107→				return
   108→			}
   109→		}
   110→	}
   111→}
