    12â†’type ChatCompletionErrorHandler interface {
    13â†’	// HandlerError handles error in non-stream response, should return the proper error format to client.
    14â†’	HandlerError(c *gin.Context, err error)
    15â†’}
    16â†’
    17â†’type ChatCompletionSSEHandlers struct {
    18â†’	ChatCompletionProcessor *chat.ChatCompletionProcessor
    19â†’	ErrorHandler            ChatCompletionErrorHandler
    20â†’}
    21â†’
    22â†’func NewChatCompletionHandlers(processor *chat.ChatCompletionProcessor) *ChatCompletionSSEHandlers {
    23â†’	return &ChatCompletionSSEHandlers{
    24â†’		ChatCompletionProcessor: processor,
    25â†’	}
    26â†’}
    27â†’
    28â†’func (handlers *ChatCompletionSSEHandlers) ChatCompletion(c *gin.Context) {
    29â†’	ctx := c.Request.Context()
    30â†’
    31â†’	// Use ReadHTTPRequest to parse the request
    32â†’	genericReq, err := httpclient.ReadHTTPRequest(c.Request)
    33â†’	if err != nil {
    34â†’		handlers.ErrorHandler.HandlerError(c, err)
    35â†’		return
    36â†’	}
    37â†’
    38â†’	result, err := handlers.ChatCompletionProcessor.Process(ctx, genericReq)
    39â†’	if err != nil {
    40â†’		handlers.ErrorHandler.HandlerError(c, err)
    41â†’		return
    42â†’	}
    43â†’
    44â†’	if result.ChatCompletion != nil {
    45â†’		resp := result.ChatCompletion
    46â†’
    47â†’		contentType := "application/json"
    48â†’		if ct := resp.Headers.Get("Content-Type"); ct != "" {
    49â†’			contentType = ct
    50â†’		}
    51â†’
    52â†’		c.Data(resp.StatusCode, contentType, resp.Body)
    53â†’
    54â†’		return
    55â†’	}
    56â†’
    57â†’	if result.ChatCompletionStream != nil {
    58â†’		defer func() {
    59â†’			log.Debug(ctx, "Close chat stream")
    60â†’
    61â†’			err := result.ChatCompletionStream.Close()
    62â†’			if err != nil {
    63â†’				logger.Error(ctx, "Error closing stream", log.Cause(err))
    64â†’			}
    65â†’		}()
    66â†’
    67â†’		// Set SSE headers
    68â†’		c.Header("Content-Type", "text/event-stream")
    69â†’		c.Header("Cache-Control", "no-cache")
    70â†’		c.Header("Connection", "keep-alive")
    71â†’		c.Header("Access-Control-Allow-Origin", "*")
    72â†’
    73â†’		writeSSEStream(c, result.ChatCompletionStream)
    74â†’	}
    75â†’}
