    77â†’func writeSSEStream(c *gin.Context, stream streams.Stream[*httpclient.StreamEvent]) {
    78â†’	ctx := c.Request.Context()
    79â†’	clientDisconnected := false
    80â†’
    81â†’	defer func() {
    82â†’		if clientDisconnected {
    83â†’			log.Warn(ctx, "Client disconnected")
    84â†’		}
    85â†’	}()
    86â†’
    87â†’	clientGone := c.Writer.CloseNotify()
    88â†’
    89â†’	for {
    90â†’		select {
    91â†’		case <-clientGone:
    92â†’			clientDisconnected = true
    93â†’			log.Warn(ctx, "Client disconnected")
    94â†’			return
    95â†’		default:
    96â†’			if stream.Next() {
    97â†’				cur := stream.Current()
    98â†’				c.SSEvent(cur.Type, cur.Data)
    99â†’				log.Debug(ctx, "write stream event", log.Any("event", cur))
   100â†’				c.Writer.Flush()
   101â†’			} else {
   102â†’				if stream.Err() != nil {
   103â†’					log.Error(ctx, "Error in stream", log.Cause(stream.Err()))
   104â†’					c.SSEvent("error", stream.Err())
   105â†’				}
   106â†’
   107â†’				return
   108â†’			}
   109â†’		}
   110â†’	}
   111â†’}
